mapper.update:
UPDATE PRODUCT SET
PROD_NAME = #{prod_name}, BRAND = #{brand}, CATEGORY = #{category}, PRICE = #{price}, DISCOUNT_PRICE = #{discount_price}, CAPACITY = #{capacity}, SCENT = #{scent}, FEEL = #{feel}, PRODUCT_EXPLAIN = #{product_explain}, EXPLAIN_URL = #{explain_url},
STOCK = #{stock}, COST_PRICE = #{cost_price} WHERE PROD_NO = #{prod_no}

mapper.select:
SELECT I.INQUIRY_ID INQUIRY_ID, P.PROD_NO PROD_NO, P.PROD_NAME PROD_NAME, P.IMG_URL IMG_URL, I.CUSTOMER_ID CUSTOMER_ID, I.CONTENTS CONTENTS, I.QNADAY QNADAY, R.CONTENTS REPLY_CONTENTS, R.REPLYDAY REPLYDAY FROM INQUIRY I INNER JOIN PRODUCT P ON I.PROD_NO = P.PROD_NO LEFT OUTER JOIN INQUIRY_REPLY R ON I.INQUIRY_ID = R.INQUIRY_ID
WHERE I.INQUIRY_ID = #{inquiry_id}
ORDER BY QNADAY DESC
